mapper.insert:
INSERT INTO guardian_patient_connection (
patient_no, guardian1_no, guardian2_no, guardian3_no
) VALUES (
#{patientNo}, #{guardian1No}, #{guardian2No}, #{guardian3No}
)
ON CONFLICT (patient_no) DO NOTHING

mapper.update:
UPDATE guardian_patient_connection
SET guardian1_no = #{guardianNo}
WHERE patient_no = #{patientNo}

mapper.select:
SELECT EXISTS (
  SELECT 1
  FROM guardian_patient_connection
  WHERE guardian1_no = #{value}
     OR guardian2_no = #{value}
     OR guardian3_no = #{value}
)

mapper.update:
UPDATE guardian_patient_connection c
SET
  guardian1_no = CASE
    WHEN c.guardian1_no IS NULL THEN #{guardianNo}
    ELSE c.guardian1_no
  END,
  guardian2_no = CASE
    WHEN c.guardian1_no IS NOT NULL AND c.guardian2_no IS NULL THEN #{guardianNo}
    ELSE c.guardian2_no
  END,
  guardian3_no = CASE
    WHEN c.guardian1_no IS NOT NULL AND c.guardian2_no IS NOT NULL AND c.guardian3_no IS NULL THEN #{guardianNo}
    ELSE c.guardian3_no
  END
WHERE c.patient_no = #{patientNo}
  AND NOT EXISTS (
    SELECT 1
    FROM guardian_patient_connection x
    WHERE x.patient_no = c.patient_no
      AND (x.guardian1_no = #{guardianNo}
        OR x.guardian2_no = #{guardianNo}
        OR x.guardian3_no = #{guardianNo})
  )
  AND NOT EXISTS (
    SELECT 1
    FROM guardian_patient_connection y
    WHERE (y.guardian1_no = #{guardianNo}
        OR y.guardian2_no = #{guardianNo}
        OR y.guardian3_no = #{guardianNo})
      AND y.patient_no != c.patient_no
  )

UPDATE guardian_patient_connection c
SET guardian1_no = #{guardianNo}
WHERE c.patient_no = #{patientNo}
  AND NOT EXISTS (
    SELECT 1
    FROM guardian_patient_connection y
    WHERE (y.guardian1_no = #{guardianNo}
        OR y.guardian2_no = #{guardianNo}
        OR y.guardian3_no = #{guardianNo})
      AND y.patient_no != c.patient_no
  )

mapper.select:
SELECT CASE WHEN EXISTS (
  SELECT 1 FROM users WHERE user_no = #{value} AND role_no = 3
) THEN TRUE ELSE FALSE END

SELECT CASE WHEN EXISTS (
  SELECT 1
  FROM guardian_patient_connection
  WHERE patient_no = #{patientNo}
    AND (
      guardian1_no = #{guardianNo}
      OR guardian2_no = #{guardianNo}
      OR guardian3_no = #{guardianNo}
    )
) THEN TRUE ELSE FALSE END
