mapper.delete:
DELETE FROM neighbor_friends
WHERE user_no_1 = #{userNo1}
  AND user_no_2 = #{userNo2}

mapper.select:
SELECT COUNT(*)
FROM neighbor_friends
WHERE user_no_1 = #{userNo1}
  AND user_no_2 = #{userNo2}

SELECT user_no
FROM users
WHERE user_id = #{userId}

SELECT COUNT(*)
FROM plaza
WHERE plaza_no = #{plazaNo}
  AND user_no = #{userNo}

SELECT COUNT(*)
FROM plaza
WHERE plaza_no = #{plazaNo}
  AND user_no = #{userNo}
  AND member_name = '방장'

mapper.delete:
DELETE FROM plaza
WHERE plaza_no = #{plazaNo}
  AND user_no = #{userNo}

mapper.select:
SELECT COUNT(DISTINCT plaza_no)
FROM plaza
WHERE user_no = #{userNo}

mapper.update:
UPDATE post
SET title = #{title}, content = #{content}
WHERE post_id = #{noticeNo}
AND plaza_no = #{plazaNo}
AND notice_type = '공지'

mapper.delete:
DELETE FROM post 
WHERE post_id = #{noticeNo}
AND notice_type = '공지'
