mapper.insert:
INSERT INTO users (
    user_id, user_pw, name, birth_date,
    phone_number, invitation_code, role_no
) VALUES (
    #{userId}, #{userPw}, #{name}, #{birthDate},
    #{phoneNumber}, #{invitationCode}, #{roleNo}
)

mapper.update:
UPDATE users
name = #{name}, user_pw = #{userPw}, birth_date = #{birthDate, jdbcType=DATE}, phone_number = #{phoneNumber}, invitation_code = #{invitationCode}, subscription_status = #{subscriptionStatus}, user_status = #{userStatus}, profile_photo = #{profilePhoto}, role_no = #{roleNo},
WHERE user_no = #{userNo}
